const express = require("express"),
  { engine: engine } = require("express-handlebars"),
  bodyParser = require("body-parser"),
  path = require("path"),
  skills = require("./data/skills.json"),
  app = express(),
  HTTP_PORT = process.env.PORT || 3e3;
app.engine("handlebars", engine()),
  app.set("view engine", "handlebars"),
  app.set("views", path.join(__dirname, "/views")),
  app.use(bodyParser.urlencoded({ extended: !1 })),
  app.use(bodyParser.json()),
  app.use(express.static("build/public")),
  app.get("/", (e, s) => {
    s.render("index", { title: "Joeward Peralta", skills: skills.skills });
  }),
  app.get("/contact", (e, s, r) => {
    s.render("contact");
  }),
  app.use((e, s) => {
    s.render("under-construction");
  }),
  app.listen(HTTP_PORT, () => {
    console.log(`Listening to port ${HTTP_PORT}`);
  });
